import TheHeadingNavbar from "@/components/heading/TheHeadingNavbar.vue";
export default {
  components: {
    TheHeadingNavbar
  },
  computed: {
    appbarStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'sm':
          return "height:30px;";

        case 'md':
          return "font-size:28px;position:relative;";

        case 'lg':
          return "9px;";

        case 'xl':
          return "10px;";
      }
    }

  }
};
