import { getPicturesGroupByNames } from "@/http/api/picture";
export default {
  name: "carousel",
  props: {
    height: {
      type: String,
      required: true
    },
    componentName: {
      type: String,
      required: true
    },
    pageName: {
      type: String,
      required: true
    }
  },

  data() {
    return {
      drawer: true,
      presentationCarouselSlides: []
    };
  },

  created() {
    this.API_URL = process.env.VUE_APP_API_URL;
    getPicturesGroupByNames(this.pageName, "carousel", this.componentName).then(data => {
      this.presentationCarouselSlides = data;
    });
  },

  computed: {
    setWidth() {
      const w = this.$vuetify.breakpoint.width,
            h = this.$vuetify.breakpoint.height;

      if (h > w) {
        switch (this.$vuetify.breakpoint.name) {
          case "xs":
          case "sm":
            return w * 0.85;

          case "md":
            return w * 0.6;

          default:
            return w * 0.5;
        }
      } else {
        return w * 0.4;
      }
    }

  }
};
