export default {
  name: "TheHeadingNavbar",
  computed: {
    fontSize() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "title";

        case 'sm':
          return "h4";

        case 'md':
          return "body-1";

        case 'lg':
          return "h6";

        case 'xl':
          return "h4";
      }
    }

  }
};
