export default {
  computed: {
    appbarStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'sm':
          return "";

        case 'md':
          return "font-size:28px;position:relative;";

        case 'lg':
          return "9px;";

        case 'xl':
          return "10px;";
      }
    },

    fontStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "font-size:10px;position:relative;float: left !important;";

        case 'sm':
          return "font-size:22px;";

        case 'md':
          return "font-size:28px;";

        case 'lg':
          return "9px;";

        case 'xl':
          return "10px;";
      }
    }

  }
};
