import ResizeText from "vue-resize-text";
export default {
  name: "TheHeadingNavbar",
  directives: {
    ResizeText
  },
  computed: {
    height() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return 20;

        case 'sm':
          return 40;

        case 'md':
          return 50;

        case 'lg':
          return 60;

        case 'xl':
          return 80;
      }
    }

  }
};
