import velocityMixin from "@/mixins/velocityMixin.ts";
import Velocity from "velocity-animate";
export default {
  name: "TheIndexViewCarousel",
  data: () => ({
    drawer: true,
    slides: [require("../../../../../assets/images/index_carousel_metallica.png"), require("../../../../../assets/images/index_carousel_slipknot.png"), require("../../../../../assets/images/index_carousel_lamb_of_god.png"), require("../../../../../assets/images/index_carousel.png")]
  }),
  methods: {
    toggleDrawer: () => this.drawer = !this.drawer,
    beforeEnter: function (element) {
      element.style.opacity = 0;
      element.style.transformOrigin = 'left';
    },
    enter: function (element, done) {
      Velocity(element, {
        opacity: 1
      }, {
        duration: 750
      });
      Velocity(element, {
        complete: done
      });
    },
    leave: function (element, done) {
      Velocity(element, {
        translateX: '15px',
        rotateZ: '50deg'
      }, {
        duration: 300
      });
      Velocity(element, {
        rotateZ: '100deg'
      }, {
        loop: 1
      });
      Velocity(element, {
        rotateZ: '45deg',
        translateY: '30px',
        translateX: '30px',
        opacity: 0
      }, {
        complete: done
      });
    }
  }
};
