import TheIndexViewCarousel from "@/components/carousels/TheIndexViewCarousel.vue";
import velocityMixin from "@/mixins/velocityMixin";
export default {
  name: "TheContent",
  data: () => ({
    presentationCarouselDrawer: true
  }),
  components: {
    TheIndexViewCarousel
  },
  props: {
    height: {
      type: String,
      required: true
    }
  },
  methods: {
    callback() {
      console.log(document.getElementsByClassName("dddd")[0].getBoundingClientRect().y * 100 / this.$vuetify.breakpoint.height > 1);
      this.presentationCarouselDrawer = document.getElementsByClassName("dddd")[0].getBoundingClientRect().y * 100 / this.$vuetify.breakpoint.height > 1;
    }

  },
  computed: {
    appbarHeight: function () {
      switch (this.$vuetify.breakpoint.name) {
        case "xs":
          return String(this.$vuetify.breakpoint.width * 0.3473);

        case "sm":
          return String(this.$vuetify.breakpoint.width * 0.3773);

        case "lg":
          return String(this.$vuetify.breakpoint.width * 0.2973);

        default:
          return String(this.$vuetify.breakpoint.width * 0.3173);
      }
    }
  },
  mixins: [velocityMixin]
};
