export default {
  data: () => ({
    drawer: false
  }),
  computed: {
    iconsSize() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return 10;

        case 'sm':
          return 15;

        case 'md':
          return 20;

        case 'lg':
          return 25;

        case 'xl':
          return 30;
      }
    }

  },
  methods: {
    setDrawer() {
      this.drawer = !this.drawer;
    }

  }
};
