export default {
  name: "TheIndexViewCarousel",
  data: () => ({
    drawer: true,
    colors: ['green', 'secondary', 'yellow darken-4', 'red lighten-2', 'orange darken-1'],
    labels: ["nu-metal", "old-school"],
    slides: [require("../../../../../assets/images/index_carousel_metallica.png"), require("../../../../../assets/images/index_carousel_slipknot.png"), require("../../../../../assets/images/index_carousel_lamb_of_god.png"), require("../../../../../assets/images/index_carousel.png")]
  }),
  methods: {
    beforeEnter: function (el) {
      el.style.opacity = 0;
      el.style.transformOrigin = 'left';
    },
    enter: function (el, done) {
      Velocity(el, {
        opacity: 1,
        fontSize: '1.4em'
      }, {
        duration: 300
      });
      Velocity(el, {
        fontSize: '1em'
      }, {
        complete: done
      });
    },
    leave: function (el, done) {
      Velocity(el, {
        translateX: '15px',
        rotateZ: '50deg'
      }, {
        duration: 200
      });
      Velocity(el, {
        rotateZ: '100deg'
      }, {
        loop: 1
      });
      Velocity(el, {
        rotateZ: '45deg',
        translateY: '30px',
        translateX: '30px',
        opacity: 0
      }, {
        complete: done
      });
    }
  }
};
