export default {
  name: "TheHeadingNavbar",
  computed: {
    fontStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "font-size:10px;position:relative;top:-8px !important;";

        case 'sm':
          return "font-size:22px;position:relative;top:-4px !important;";

        case 'md':
          return "font-size:28px;position:relative;";

        case 'lg':
          return "9px;";

        case 'xl':
          return "10px;";
      }
    }

  }
};
