export default {
  data() {
    return {
      drawer: false
    };
  },

  computed: {
    iconsSize() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return 10;

        case 'sm':
          return 15;

        case 'md':
          return 20;

        case 'lg':
          return 25;

        case 'xl':
          return 30;
      }
    },

    toolbarTitleStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "font-size:9px;position:relative;top: -6px !important;left: 12px !important;";

        case 'sm':
          return "font-size:12px;position:relative;top: -6px !important;left: 12px !important;";

        case 'md':
          return "font-size:16px;position:relative;top: -4px !important;left: 12px !important;";

        case 'lg':
          return "font-size:22px;position:relative;left: -15px !important;";

        case 'xl':
          return "font-size:28px;position:relative;top: 7px !important;left: 12px !important;";
      }
    }

  }
};
