export default {
  data() {
    return {
      isShow: false
    };
  },

  computed: {
    iconsStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "width: 10px !important;height:10px !important; position: relative; top:6px; left:12px;";

        case 'sm':
          return "font-size:12px;position:relative;top: -6px !important;left: -22px !important;";

        case 'md':
          return "font-size:16px;position:relative;top: -4px !important;left: -22px !important;";

        case 'lg':
          return "font-size:22px;position:relative;left: -22px !important;";

        case 'xl':
          return "font-size:28px;position:relative;top: 7px !important;left: -22px !important;";
      }
    },

    fontStyle() {
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return "font-size:9px;position:relative;top: -6px !important;left: -22px !important;";

        case 'sm':
          return "font-size:12px;position:relative;top: -6px !important;left: -22px !important;";

        case 'md':
          return "font-size:16px;position:relative;top: -4px !important;left: -22px !important;";

        case 'lg':
          return "font-size:22px;position:relative;left: -22px !important;";

        case 'xl':
          return "font-size:28px;position:relative;top: 7px !important;left: -22px !important;";
      }
    }

  }
};
