import TheHeading from "@/components/heading/TheHeading.vue";
import TheContent from "@/components/content/TheContent.vue";
import CarouselsList from "@/components/carousels/CarouselsList.vue";
import { TweenMax } from "gsap";
export default {
  name: "IndexView",
  components: {
    TheContent,
    TheHeading,
    CarouselsList
  },
  computed: {
    headerHeight() {
      switch (this.$vuetify.breakpoint.name) {
        case "lg":
        case "xl":
          var height = this.$vuetify.breakpoint.height * 0.4084;
          return height;

        default:
          var height = this.$vuetify.breakpoint.height * 0.1884;
          return height;
      }
    },

    //@ts-ignore
    height() {
      //@ts-ignore
      return this.$vuetify.breakpoint.height;
    }

  },

  beforeRouteEnter(el) {
    TweenMax.to(el, 0, {
      yPercent: -100,
      scale: 0.9,
      ease: Power2.easeInOut
    });
  },

  transition: {
    mode: "in-out",
    css: false,

    beforeEnter(el) {
      TweenMax.to(el, 0, {
        yPercent: -100,
        scale: 0.9,
        ease: Power2.easeInOut
      });
    },

    enter(el, done) {
      done();
      TweenMax.to(el, 1, {
        yPercent: -50,
        scale: 0.9,
        ease: Power2.easeInOut,
        onComplete: function () {
          TweenMax.to(el, 1, {
            yPercent: 0,
            scale: 1,
            ease: Power2.easeInOut
          });
        }
      });
    },

    leave(el, done) {
      TweenMax.to(el, 1, {
        yPercent: -50,
        scale: 0.9,
        ease: Power2.easeInOut,
        onComplete: function () {
          TweenMax.to(el, 1, {
            yPercent: -100,
            scale: 1,
            ease: Power2.easeInOut,
            onComplete: function () {
              done();
            }
          });
        }
      });
    }

  }
};
