import Modal from "../Modal.vue";
export default {
  name: "content",
  components: {
    Modal
  },
  data: () => ({
    activeState: "unactive",
    overlay: false,
    isBlurModeState: false
  }),
  props: {
    pageName: {
      type: String,
      required: true
    }
  },

  mounted() {
    this.$root.$on("onContentBlurToggle", () => {
      this.isBlurModeState = !this.isBlurModeState;
    });
    this.$on(["onModalActivated", "onModalDeactivated"], () => {
      this.overlay = !this.overlay;
      this.activeState = !this.overlay ? "unactive" : "active";
      console.log(this.overlay);
      console.log("LLLLLLLLLLLLLLLLLLL");
    });
    this.$on(["onModalDeactivated"], () => {});
