export default {
  name: "TheContent",
  data: () => ({
    presentationCarouselDrawer: true
  }),
  props: {
    pageName: {
      type: String,
      required: true
    }
  },
  computed: {
    //@ts-ignore
    headerHeight() {
      //@ts-ignore
      switch (this.$vuetify.breakpoint.name) {
        case "lg":
        case "xl":
          //@ts-ignore
          return this.$vuetify.breakpoint.height * 0.4084;

        default:
          //@ts-ignore
          var height = this.$vuetify.breakpoint.height * 0.1884;
          return height;
      }
    }

  }
};
