export default {
  name: "the-authorization-button-menu",
  props: {
    iconsSize() {
      return {
        iconsSize: Number
      };
    }

  },
  data: () => ({
    items: [{
      title: 'register'
    }, {
      title: 'login'
    }],
    visible: false
  }),
  computed: {
    iconsSize() {
      console.log(this.$vuetify.breakpoint);

      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return 13;

        case 'sm':
          return 18;

        case 'md':
          return 22;

        case 'lg':
          return 27;

        case 'xl':
          return 32;
      }
    }

  },
  methods: {
    onClick() {
      this.visible = !this.visible;
    }

  }
};
