import TheHeadingButton from "@/components/heading/HeadingButton.vue";
import Consumer from "@/context/Consumer.vue";
import Provider from "@/context/Provider.vue";
import TheAuthorizationForm from "@/components/authorizationForm/TheAuthorizationForm.vue";
import TheSideBar from "@/components/sidebar/TheSideBar.vue";
import theHeadingMixin from "@/mixins/theHeadingMixin";
import { getPicturesGroupByNames } from "@/http/api/picture";
export default {
  components: {
    TheSideBar,
    Provider,
    Consumer,
    TheHeadingButton,
    TheAuthorizationForm
  },
  mixins: [theHeadingMixin],
  props: {
    height: {
      type: Number,
      required: true
    },
    width: {
      type: Number,
      required: true
    },
    pageName: {
      type: String,
      required: true
    }
  },

  async created() {
    // @ts-ignore
    this.maxHeight = this.$vuetify.breakpoint.height * 0.52; // @ts-ignore

    this.minHeight = this.$vuetify.breakpoint.height * 0.12; // @ts-ignore

    await getPicturesGroupByNames(this.pageName, "header").then(data => {
      // @ts-ignore
      this.headerPath = `${process.env.VUE_APP_API_URL}/index_page/header/${data[0]}`;
    });
  },

  computed: {
    // @ts-ignore
    iconsSize: function () {
      // @ts-ignore
      switch (this.$vuetify.breakpoint.name) {
        case 'xs':
          return 14;

        case 'sm':
          return 20;

        case 'md':
          return 22;

        case 'lg':
          return 25;

        case 'xl':
          return 30;
      }
    }
  }
};
