import { getPicturesGroupByNames } from "@/http/api/picture";
import TheCarouselMixin from "@/mixins/carouselMixin";
export default {
  name: "carousel",
  mixins: {
    TheCarouselMixin
  },
  props: {
    componentName: {
      type: String,
      required: true
    },
    pageName: {
      type: String,
      required: true
    }
  },

  mounted() {
    this.$emit("mounted", this.setHeight);
  },

  created() {
    this.API_URL = process.env.VUE_APP_API_URL;
    getPicturesGroupByNames(this.pageName, "carousel", this.componentName).then(data => {
      console.log(this.pageName);
      this.presentationCarouselSlides = data;
    });
  }

};
